let eye_ref=document.querySelectorAll(".iris"),events=["mousemove","touchmove"];function isTouchDevice(){try{return document.createEvent("TouchEvent"),!0}catch(e){return!1}}events.forEach((e=>{document.body.addEventListener(e,(e=>{eye_ref.forEach((t=>{let n=-(window.innerWidth/2-e.pageX)/20,r=-(window.innerHeight/2-e.pageY)/45;t.style.transform=`rotate(-45deg) translateY(${r}px) translateX(${n}px)`}))}))}));